sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var requestUser = request.pathParams.user_name;

	var requestFAQuery = request.queryParams.fa_query;

	var requestUserName = new GlideRecord('sys_user');

	requestUserName.get('user_name', requestUser);


	var userFACount = new GlideAggregate('x_375416_first_app_first_table');

	userFACount.addAggregate('COUNT');
	userFACount.addQuery('u_requested_for', requestUserName.sys_id);
	userFACount.addEncodedQuery(requestFAQuery);
	userFACount.groupBy('u_requested_type');
	userFACount.query();

	var faRecs = 0;
	var faUserRecs = [];
	var faRecsCount = 0;

	while(userFACount.next()) {
		faRecs = userFACount.getAggregate('COUNT');
		var reqType = userFACount.u_requested_type.getDisplayValue();
		faUserRecs.push({"reuqestType": reqType, "count": faRecs});
		faRecsCount = parseInt(faRecsCount)+ parseInt(faRecs);
	}

	var body = {};
	body.totalUserFARecs = faRecsCount;
	body.catCounts = faUserRecs;
	body.user = {"Username": requestUserName.user_name, "UserId": requestUserName.sys_id};

	response.setBody(body);


})(request, response);
